import React, { useState } from 'react';

const CreateCredentialForm = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: '',
    skills: '',
    eqfLevel: '',
  });

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form data submitted:', formData);
    // Hier später: fetch('/api/credentials', {...})
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Create a Microcredential</h2>

      <label>Title:</label>
      <input type="text" name="title" value={formData.title} onChange={handleChange} required />

      <label>Description:</label>
      <textarea name="description" value={formData.description} onChange={handleChange} required />

      <label>Category:</label>
      <input type="text" name="category" value={formData.category} onChange={handleChange} />

      <label>Skills (comma-separated):</label>
      <input type="text" name="skills" value={formData.skills} onChange={handleChange} />

      <label>EQF Level:</label>
      <select name="eqfLevel" value={formData.eqfLevel} onChange={handleChange}>
        <option value="">--Select Level--</option>
        {[1,2,3,4,5,6,7,8].map(level => (
          <option key={level} value={level}>{level}</option>
        ))}
      </select>

      <button type="submit">Submit</button>
    </form>
  );
};

export default CreateCredentialForm;
